mapper.select:
SELECT id, name, ci_path, description, rank_score, kind, use_yn, create_timestamp, modified_timestamp
FROM team_info
WHERE name = #{name}

mapper.update:
UPDATE team_info
SET use_yn = 'N'
WHERE id = #{teamId}

mapper.select:
SELECT id, name, team_id, latitude, longitude, legal_code, jibun_address, road_address
FROM hometown_info
WHERE team_id = #{teamId}

SELECT id, team_id, day_of_week, from_time, to_time
FROM play_time_info
WHERE team_id = #{teamId}

mapper.update:
UPDATE team_info
SET modified_timestamp = NOW()

    , name = #{name}


    , description = #{description}

WHERE id = #{id}

UPDATE play_time_info
SET  from_time = #{playTimeFrom}
  ,to_time = #{playTimeTo}
  ,day_of_week = #{dayOfWeek}
WHERE id = #{id}
  AND team_id = #{teamId}

UPDATE hometown_info
SET  jibun_address = #{jibunAddress}
  ,road_address = #{roadAddress}
  ,latitude = #{latitude}
  ,longitude = #{longitude}
  ,legal_code = #{legalCode}
  ,name=#{name}
WHERE id = #{id}
  AND team_id = #{teamId}

UPDATE team_user_mapping
SET role_code = #{role}
WHERE team_id = #{teamId}
  AND user_id = #{userId}
  AND use_yn = 'Y'

mapper.select:
SELECT a.id , a.name , a.ci_path , a.description , a.rank_score , a.kind FROM team_info a
INNER JOIN hometown_info b ON a.id = b.team_id
AND ((b.road_address LIKE CONCAT('%',#{addressSi},'%') AND b.road_address LIKE CONCAT('%',#{addressGungu},'%')) OR (b.jibun_address LIKE CONCAT('%',#{addressSi},'%') AND b.jibun_address LIKE CONCAT('%',#{addressGungu},'%')))
INNER JOIN play_time_info c ON a.id = c.team_id
AND c.day_of_week = #{dayOfWeek}
AND #{time} BETWEEN c.from_time AND c.to_time
WHERE a.use_yn = 'Y'
AND rank_score >= #{fromScore}
AND rank_score <= #{toScore}
AND name like CONCAT('%',#{name},'%')
LIMIT #{offset}, #{count}

SELECT id
FROM team_user_mapping
WHERE user_id = #{userId}
  AND team_id = #{teamId}
  AND use_yn = 'Y'

SELECT a.id, a.user_id, a.team_id, a.process_yn, a.description
FROM join_request a
WHERE team_id = #{teamId}
  AND user_id = #{userId}
  AND process_yn = 'N'

mapper.update:
UPDATE team_user_mapping
SET  use_yn = 'N'
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.select:
SELECT b.id, b.name, b.ci_path, b.description, b.rank_score, b.kind, b.use_yn, b.create_timestamp, b.modified_timestamp
FROM matching_request_info a INNER JOIN team_info b ON a.team_id = b.id
WHERE a.id = #{matchingRequestId}

mapper.update:
UPDATE team_info
SET rank_score = #{score}
WHERE id = #{teamId}

UPDATE team_info
SET ci_path = #{ciPath}
WHERE id = #{teamId}
